package fr.loudo.cameratester.skins;

import com.mojang.authlib.properties.Property;

public class SkinConstants {

    public static final Property STEVE_SKIN = new Property(
            "textures",
            "",
            ""
    );
    public static final Property ALEX_SKIN = new Property(
            "textures",
            "",
            ""
    );
    public static final Property HEROBRINE_SKIN = new Property(
            "textures",
            "",
            ""
    );


    public static Property getSkinByName(String playerName) {
        switch (playerName) {
            case "steve":
                return STEVE_SKIN;
            case "alex":
                return ALEX_SKIN;
            case "herobrine":
                return HEROBRINE_SKIN;
        }
        return null;
    }
}
